<?php
  setcookie("sigtron", $_POST["template"] . "###" . $_POST["name"] . "###" . $_POST["title"] . "###" . $_POST["phone"] . "###" . $_POST["additional"], time() + (86400 * 365));
  $file = file_get_contents($_POST["template"] . ".template");
  $file = str_replace("###NAME###", $_POST["name"], $file);
  $file = str_replace("###TITLE###", $_POST["title"], $file);
  $file = str_replace("###PHONE###", $_POST["phone"], $file);
  if ($_POST["additional"]) {
    $_POST["additional"] .= "<br />";
  }
  $file = str_replace("###ADDITIONAL###", $_POST["additional"], $file);
  $file = str_replace("###FBICON###", '<img width="32" height="32" alt="Facebook" style="margin-top: 10px" src="data:image/png;base64," />', $file);
  $file = str_replace("###TWICON###", '<img width="32" height="32" alt="Twitter" src="data:image/png;base64," />', $file);
  $file = str_replace("###IGICON###", '<img width="32" height="32" alt="Instagram" src="data:image/png;base64," />', $file);
  $file = str_replace("###LIICON###", '<img width="32" height="32" alt="LinkedIn" src="data:image/png;base64," />', $file);
  $file = str_replace("###DONATEICON###", '<img alt="Donate" src="data:image/png;base64," />', $file);
  $file = str_replace("###PVLOGO###", '<img alt="Logo" src="data:image/png;base64,">', $file);

  echo $file;
?>